import React, { useState, useEffect, useRef } from 'react';
import { Send, RefreshCw, Copy } from 'lucide-react';

// Placeholder logo - you'd replace with your actual logo
const Logo = () => (
  <div className="text-2xl font-bold text-blue-600">
    CryptoSage
  </div>
);

// Suggestions component
const Suggestions = ({ onSuggestionSelect }) => {
  const suggestions = [
    "Should I buy Solana now?",
    "What's happening in the crypto market?",
    "Analyze Ethereum's current conditions",
    "Bitcoin investment outlook"
  ];

  return (
    <div className="grid grid-cols-2 gap-2 p-4">
      {suggestions.map((suggestion, index) => (
        <button 
          key={index}
          className="bg-gray-100 hover:bg-gray-200 text-left p-3 rounded-lg text-sm transition-colors"
          onClick={() => onSuggestionSelect(suggestion)}
        >
          {suggestion}
        </button>
      ))}
    </div>
  );
};

// Main Chat Component
const CryptoAnalyzerChat = () => {
console.log('CryptoAnalyzerChat component rendering');
  const [query, setQuery] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e) => {
    e?.preventDefault();
    
    if (!query.trim()) return;

    // Add user message
    const userMessage = { 
      type: 'user', 
      content: query,
      timestamp: new Date().toLocaleString()
    };
    setMessages(prev => [...prev, userMessage]);

    // Prepare request
    const requestBody = {
      message: query,
      context: {
        risk_tolerance: "medium",
        investment_horizon: "long",
        portfolio: ["ETH", "SOL", "ADA"]
      }
    };

    setIsLoading(true);
    setQuery('');

    try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}/analyze`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestBody)
          });

      if (!response.ok) {
        throw new Error('Analysis failed');
      }

      const data = await response.json();

      // Add AI response
      const aiMessage = {
        type: 'ai',
        content: data.response,
        coin: data.coin || 'Market',
        timestamp: new Date(data.timestamp * 1000).toLocaleString()
      };
      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        type: 'error',
        content: 'Sorry, something went wrong. Please try again.',
        timestamp: new Date().toLocaleString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCopyMessage = (message) => {
    navigator.clipboard.writeText(message);
  };

  return (
    <div className="flex flex-col h-screen max-w-4xl mx-auto bg-white shadow-lg">
      {/* Header */}
      <div className="flex justify-between items-center p-4 border-b">
        <Logo />
        <div className="text-gray-500">Powered by DeepSeek</div>
      </div>

      {/* Messages Area */}
      <div className="flex-grow overflow-y-auto p-4 space-y-4">
        {messages.length === 0 ? (
          <Suggestions onSuggestionSelect={(suggestion) => {
            setQuery(suggestion);
            handleSubmit({ preventDefault: () => {} });
          }} />
        ) : (
          messages.map((message, index) => (
            <div 
              key={index} 
              className={`flex ${
                message.type === 'user' 
                  ? 'justify-end' 
                  : 'justify-start'
              }`}
            >
              <div 
                className={`max-w-[80%] p-3 rounded-lg ${
                  message.type === 'user'
                    ? 'bg-blue-500 text-white'
                    : message.type === 'error'
                    ? 'bg-red-100 text-red-800'
                    : 'bg-gray-100'
                }`}
              >
                <div className="text-sm mb-1">
                  {message.type === 'ai' && (
                    <span className="font-semibold text-blue-600 mr-2">
                      {message.coin} Analysis
                    </span>
                  )}
                  <span className="text-xs text-gray-500">
                    {message.timestamp}
                  </span>
                </div>
                <div className="whitespace-pre-wrap">{message.content}</div>
                {message.type !== 'user' && (
                  <button 
                    onClick={() => handleCopyMessage(message.content)}
                    className="mt-2 text-xs text-gray-500 hover:text-gray-700 flex items-center"
                  >
                    <Copy size={12} className="mr-1" /> Copy
                  </button>
                )}
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <form 
        onSubmit={handleSubmit} 
        className="p-4 border-t flex items-center space-x-2"
      >
        <input 
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Ask about cryptocurrency markets..."
          disabled={isLoading}
          className="flex-grow p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button 
          type="submit" 
          disabled={isLoading || !query.trim()}
          className="bg-blue-500 text-white p-2 rounded-lg hover:bg-blue-600 disabled:opacity-50"
        >
          {isLoading ? <RefreshCw className="animate-spin" /> : <Send />}
        </button>
      </form>
    </div>
  );
};

export default CryptoAnalyzerChat;
