import React, { useState, useEffect, useRef } from 'react';
import { Send, Settings } from 'lucide-react';

// Suggestions Component
const Suggestions = ({ onSuggestionSelect }) => {
  const suggestions = [
    "Should I buy Solana now?",
    "What's happening in the crypto market?",
    "Should I enter a long position on ETH?",
    "Is Bitcoin a good short right now?",
    "Should I sell SOL?",
    "Analyze Cardano's price movement"
  ];

  return (
    <div className="grid grid-cols-2 md:grid-cols-3 gap-3 p-4">
      {suggestions.map((suggestion, index) => (
        <button 
          key={index}
          className="bg-gray-100 hover:bg-gray-200 text-left p-3 rounded-lg text-sm transition-colors"
          onClick={() => onSuggestionSelect(suggestion)}
        >
          {suggestion}
        </button>
      ))}
    </div>
  );
};

// User Context Form Component
const UserContextForm = ({ userContext, setUserContext, onClose }) => {
  const [localContext, setLocalContext] = useState(userContext);
  
  // Update local state when props change
  useEffect(() => {
    setLocalContext(userContext);
  }, [userContext]);
  
  // Portfolio input field management
  const [portfolioInput, setPortfolioInput] = useState('');
  
  const addPortfolioCoin = () => {
    if (portfolioInput.trim() && !localContext.portfolio.includes(portfolioInput.trim().toUpperCase())) {
      setLocalContext({
        ...localContext,
        portfolio: [...localContext.portfolio, portfolioInput.trim().toUpperCase()]
      });
      setPortfolioInput('');
    }
  };
  
  const removePortfolioCoin = (coin) => {
    setLocalContext({
      ...localContext,
      portfolio: localContext.portfolio.filter(c => c !== coin)
    });
  };
  
  const handleSave = () => {
    setUserContext(localContext);
    onClose();
  };
  
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
        <h2 className="text-xl font-bold mb-4">Your Investment Profile</h2>
        
        <div className="mb-4">
          <label className="block text-sm font-medium mb-1">Risk Tolerance</label>
          <select 
            className="w-full p-2 border rounded"
            value={localContext.risk_tolerance}
            onChange={(e) => setLocalContext({...localContext, risk_tolerance: e.target.value})}
          >
            <option value="low">Low (Conservative)</option>
            <option value="medium">Medium (Balanced)</option>
            <option value="high">High (Aggressive)</option>
          </select>
        </div>
        
        <div className="mb-4">
          <label className="block text-sm font-medium mb-1">Investment Horizon</label>
          <select 
            className="w-full p-2 border rounded"
            value={localContext.investment_horizon}
            onChange={(e) => setLocalContext({...localContext, investment_horizon: e.target.value})}
          >
           <option value="short">Short-term (less than 1 year)</option>
    <option value="medium">Medium-term (1-3 years)</option>
    <option value="long">Long-term (more than 3 years)</option>
          </select>
        </div>
        
        <div className="mb-4">
          <label className="block text-sm font-medium mb-1">Your Crypto Portfolio</label>
          <div className="flex mb-2">
            <input
              type="text"
              placeholder="Add coin (e.g., BTC)"
              className="flex-grow p-2 border rounded-l"
              value={portfolioInput}
              onChange={(e) => setPortfolioInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && addPortfolioCoin()}
            />
            <button 
              onClick={addPortfolioCoin}
              className="bg-blue-600 text-white px-3 rounded-r"
            >
              Add
            </button>
          </div>
          
          <div className="flex flex-wrap gap-2">
            {localContext.portfolio.map((coin) => (
              <div key={coin} className="bg-blue-100 px-2 py-1 rounded flex items-center">
                <span className="mr-1">{coin}</span>
                <button 
                  onClick={() => removePortfolioCoin(coin)}
                  className="text-red-600 text-xs font-bold"
                >
                  Ã—
                </button>
              </div>
            ))}
            {localContext.portfolio.length === 0 && (
              <span className="text-gray-500 text-sm">No coins added yet</span>
            )}
          </div>
        </div>
        
        <div className="flex justify-end gap-2 mt-4">
          <button 
            onClick={onClose}
            className="px-4 py-2 border rounded"
          >
            Cancel
          </button>
          <button 
            onClick={handleSave}
            className="px-4 py-2 bg-blue-600 text-white rounded"
          >
            Save Profile
          </button>
        </div>
      </div>
    </div>
  );
};

// Message formatting helper
const formatMessage = (content) => {
  if (!content) return "No content available";
  
  // Split by numbered sections with asterisks/numbers
  const parts = content.split(/(\d\.\s\*\*.*?\*\*:)/g);
  
  if (parts.length > 1) {
    return parts.map((part, index) => {
      // Check if this is a section header
      if (part.match(/\d\.\s\*\*.*?\*\*:/)) {
        return <div key={index} className="font-semibold mt-2">{part.replace(/\*\*/g, '')}</div>;
      }
      // This is section content
      return <div key={index} className="mb-2">{part}</div>;
    });
  }
  
  // Check for Markdown-style bold text
  if (content.includes('**')) {
    return content.split('\n').map((line, index) => {
      // Replace bold markdown with styled spans
      const processedLine = line.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');
      return (
        <div 
          key={index} 
          className="mb-1" 
          dangerouslySetInnerHTML={{ __html: processedLine }}
        />
      );
    });
  }
  
  // If no structured format detected, just return the content with line breaks
  return content.split('\n').map((line, index) => (
    <div key={index} className="mb-1">{line}</div>
  ));
};

// Main Chat Component
const CryptoAnalyzerChat = () => {
  const [query, setQuery] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showContextForm, setShowContextForm] = useState(false);
  const [userContext, setUserContext] = useState({
    risk_tolerance: "medium",
    investment_horizon: "medium",
    portfolio: []
  });
  
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Function to detect if query is about futures trading (long/short positions)
  const isFuturesQuery = (queryText) => {
    const futuresPatterns = [
      /\blong position\b/i,
      /\bshort position\b/i,
      /\bgo long\b/i,
      /\bgo short\b/i,
      /\benter a long\b/i,
      /\benter a short\b/i,
      /\bfutures\b/i,
      /\bleveraged\b/i,
      /\blonging\b/i,
      /\bshorting\b/i
    ];
    
    return futuresPatterns.some(pattern => pattern.test(queryText));
  };

  // Function to extract coin from message
  const extractCoin = (queryText) => {
    const coins = ['BTC', 'ETH', 'SOL', 'ADA', 'XRP', 'DOT', 'DOGE', 'AVAX', 'MATIC'];
    
    // Check for exact matches
    for (const coin of coins) {
      if (queryText.toUpperCase().includes(coin)) {
        return coin;
      }
    }
    
    // Check for common names
    const coinNames = {
      'bitcoin': 'BTC',
      'ethereum': 'ETH',
      'solana': 'SOL',
      'cardano': 'ADA',
      'ripple': 'XRP',
      'polkadot': 'DOT',
      'dogecoin': 'DOGE',
      'avalanche': 'AVAX',
      'polygon': 'MATIC'
    };
    
    const queryLower = queryText.toLowerCase();
    for (const [name, symbol] of Object.entries(coinNames)) {
      if (queryLower.includes(name)) {
        return symbol;
      }
    }
    
    // Default to BTC if no coin detected
    return 'BTC';
  };

  // Function to determine if query is about going long or short
  const extractFuturesDirection = (queryText) => {
    const queryLower = queryText.toLowerCase();
    
    // Check for long patterns
    if (/long|buy|bull/i.test(queryLower) && !/short|sell|bear/i.test(queryLower)) {
      return 'long';
    }
    
    // Check for short patterns
    if (/short|sell|bear/i.test(queryLower) && !/long|buy|bull/i.test(queryLower)) {
      return 'short';
    }
    
    // Default to "long" if direction not clear
    return 'long';
  };

  const handleSubmit = async (e) => {
    e?.preventDefault();
    
    if (!query.trim()) return;

    // Add user message
    const userMessage = { 
      type: 'user', 
      content: query,
      timestamp: new Date().toLocaleString()
    };
    setMessages(prev => [...prev, userMessage]);

    // Determine if this is a futures trading query
    const isFutures = isFuturesQuery(query);
    let apiEndpoint, requestOptions, messageType;
    
    if (isFutures) {
      // Extract coin and direction for futures trading
      const coin = extractCoin(query);
      const direction = extractFuturesDirection(query);
      
      // Create URL with query parameters
      const params = new URLSearchParams({
        action_type: 'futures',
        position: direction, // Add position parameter
        force_refresh: 'true' // Force refresh to avoid caching issues
      });
      
      apiEndpoint = `/recommendation/${coin}?${params.toString()}`;
      
      requestOptions = {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      };
      
      messageType = direction === 'long' ? 'LONG' : 'SHORT';
    } else {
      // Standard analysis query
      apiEndpoint = '/analyze';
      requestOptions = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: query,
          context: userContext // Use the user's actual context
        })
      };
      messageType = 'analysis';
    }

    setIsLoading(true);
    setQuery('');

    try {
      console.log("Sending request to:", apiEndpoint);
      const response = await fetch(apiEndpoint, requestOptions);

      if (!response.ok) {
        throw new Error('Request failed');
      }

      const data = await response.json();
      console.log("API Response:", data);

      // Format response based on endpoint type
      let aiMessage;
      
      if (isFutures) {
        // Enhanced recommendation content formatting
        const direction = extractFuturesDirection(query);
        const directionText = direction === 'long' ? 'LONG' : 'SHORT';
        
        // Custom summary based on direction
        let summary;
        if (data.action === 'BUY' && direction === 'long') {
          summary = `ENTER LONG position for ${data.coin}`;
        } else if (data.action === 'SELL' && direction === 'short') {
          summary = `ENTER SHORT position for ${data.coin}`;
        } else if (data.action === 'BUY' && direction === 'short') {
          summary = `NOT RECOMMENDED to short ${data.coin} at this time`;
        } else if (data.action === 'SELL' && direction === 'long') {
          summary = `NOT RECOMMENDED to go long on ${data.coin} at this time`;
        } else {
          summary = `NEUTRAL outlook for ${data.coin} ${directionText} position`;
        }
        
        // Get the explanation - check all possible field names
        const explanation = data.explanation || data.analysis || data.summary || "No detailed analysis available.";
        
        // Format recommendation response
        aiMessage = {
          type: 'ai',
          content: `**${data.action || 'NEUTRAL'}** - ${summary}\n\n${explanation}`,
          coin: data.coin || extractCoin(query),
          intent: messageType,
          direction: direction,
          action: data.action || 'NEUTRAL',
          timestamp: new Date().toLocaleString()
        };
      } else {
        // Format analysis response
        aiMessage = {
          type: 'ai',
          content: data.response,
          coin: data.detected_coin || 'Market',
          intent: data.detected_intent || 'Analysis',
          timestamp: new Date(data.timestamp * 1000).toLocaleString()
        };
      }
      
      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        type: 'error',
        content: 'Sorry, something went wrong. Please try again.',
        timestamp: new Date().toLocaleString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex flex-col h-screen w-full bg-white shadow-lg">
      {/* Header */}
      <div className="flex justify-between items-center p-4 border-b">
        <div className="text-2xl font-bold text-blue-600">CryptoSage</div>
        <div className="flex items-center">
          <button 
            onClick={() => setShowContextForm(true)}
            className="flex items-center text-sm text-gray-600 hover:text-blue-600 mr-4"
          >
            <Settings size={16} className="mr-1" />
            Profile
          </button>
          <div className="text-sm text-gray-500">Powered by DeepSeek</div>
        </div>
      </div>

      {/* Messages Area */}
      <div className="flex-grow overflow-y-auto p-4 space-y-4">
        <div className="max-w-6xl mx-auto w-full">
          {messages.length === 0 ? (
            <>
              <div className="mb-8 text-center">
                <h2 className="text-xl font-bold mb-2">Welcome to CryptoSage</h2>
                <p className="text-gray-600 mb-4">
                  Get AI-powered cryptocurrency analysis and trading recommendations
                </p>
                {userContext.portfolio.length > 0 && (
                  <div className="text-sm text-gray-600 mb-4">
                    <div>Your profile: {userContext.risk_tolerance} risk, {userContext.investment_horizon}-term horizon</div>
                    <div>Portfolio: {userContext.portfolio.join(', ')}</div>
                  </div>
                )}
              </div>
              <Suggestions onSuggestionSelect={(suggestion) => {
                setQuery(suggestion);
                handleSubmit({ preventDefault: () => {} });
              }} />
            </>
          ) : (
            messages.map((message, index) => (
              <div 
                key={index} 
                className={`flex ${
                  message.type === 'user' 
                    ? 'justify-end' 
                    : 'justify-start'
                }`}
              >
                <div 
                  className={`max-w-[85%] p-3 rounded-lg ${
                    message.type === 'user'
                      ? 'bg-blue-500 text-white'
                      : message.type === 'error'
                      ? 'bg-red-100 text-red-800'
                      : message.intent === 'LONG' && message.action === 'BUY'
                      ? 'bg-green-50 border border-green-200'
                      : message.intent === 'SHORT' && message.action === 'SELL'
                      ? 'bg-red-50 border border-red-200'
                      : message.intent === 'LONG' || message.intent === 'SHORT'
                      ? 'bg-yellow-50 border border-yellow-200'
                      : 'bg-gray-100'
                  }`}
                >
                  {message.type === 'ai' && (
                    <div className={`text-sm font-semibold mb-1 ${
                      message.intent === 'LONG' && message.action === 'BUY'
                        ? 'text-green-600' 
                        : message.intent === 'SHORT' && message.action === 'SELL'
                        ? 'text-red-600'
                        : message.intent === 'LONG' || message.intent === 'SHORT'
                        ? 'text-yellow-600'
                        : 'text-blue-600'
                    }`}>
                      {message.coin} {message.intent === 'LONG' || message.intent === 'SHORT' 
                        ? `${message.intent} Recommendation` 
                        : message.intent && message.intent !== 'general' 
                        ? message.intent.toUpperCase() 
                        : 'Analysis'}
                    </div>
                  )}
                  
                  <div className="text-sm whitespace-pre-wrap">
                    {message.type === 'ai' 
                      ? formatMessage(message.content) 
                      : message.content}
                  </div>
                  
                  <div className="text-xs text-gray-400 mt-2 text-right">
                    {message.timestamp}
                  </div>
                </div>
              </div>
            ))
          )}
          
          {/* Loading indicator */}
          {isLoading && (
            <div className="flex justify-center items-center p-4">
              <div className="loader w-8 h-8 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin"></div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Input Area */}
      <form 
        onSubmit={handleSubmit} 
        className="p-4 border-t"
      >
        <div className="max-w-6xl mx-auto w-full flex items-center space-x-2">
          <input 
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Ask about cryptocurrency markets or futures positions..."
            disabled={isLoading}
            className="flex-grow p-3 bg-gray-100 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button 
            type="submit" 
            disabled={isLoading || !query.trim()}
            className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition-colors disabled:opacity-50"
          >
            <Send size={20} />
          </button>
        </div>
      </form>

      {/* User Context Form Modal */}
      {showContextForm && (
        <UserContextForm 
          userContext={userContext}
          setUserContext={setUserContext}
          onClose={() => setShowContextForm(false)}
        />
      )}
    </div>
  );
};

export default CryptoAnalyzerChat;
