import React, { useState, useEffect, useRef } from 'react';
import { Send } from 'lucide-react';

// Suggestions Component
const Suggestions = ({ onSuggestionSelect }) => {
  const suggestions = [
    "Should I buy Solana now?",
    "What's happening in the crypto market?",
    "Analyze Ethereum's current conditions",
    "Bitcoin investment outlook",
    "Should I sell SOL?",
    "Should I buy Bitcoin now?"
  ];

  return (
    <div className="grid grid-cols-2 md:grid-cols-3 gap-3 p-4">
      {suggestions.map((suggestion, index) => (
        <button 
          key={index}
          className="bg-gray-100 hover:bg-gray-200 text-left p-3 rounded-lg text-sm transition-colors"
          onClick={() => onSuggestionSelect(suggestion)}
        >
          {suggestion}
        </button>
      ))}
    </div>
  );
};

// Message formatting helper
const formatMessage = (content) => {
  // Split by numbered sections with asterisks/numbers
  const parts = content.split(/(\d\.\s\*\*.*?\*\*:)/g);
  
  if (parts.length > 1) {
    return parts.map((part, index) => {
      // Check if this is a section header
      if (part.match(/\d\.\s\*\*.*?\*\*:/)) {
        return <div key={index} className="font-semibold mt-2">{part.replace(/\*\*/g, '')}</div>;
      }
      // This is section content
      return <div key={index} className="mb-2">{part}</div>;
    });
  }
  
  // If no structured format detected, just return the content
  return content;
};

// Main Chat Component
const CryptoAnalyzerChat = () => {
  const [query, setQuery] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e) => {
    e?.preventDefault();
    
    if (!query.trim()) return;

    // Add user message
    const userMessage = { 
      type: 'user', 
      content: query,
      timestamp: new Date().toLocaleString()
    };
    setMessages(prev => [...prev, userMessage]);

    // Prepare request
    const requestBody = {
      message: query,
      context: {
        risk_tolerance: "medium",
        investment_horizon: "long",
        portfolio: ["ETH", "SOL", "ADA"]
      }
    };

    setIsLoading(true);
    setQuery('');

    try {
      const response = await fetch('/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody)
      });

      if (!response.ok) {
        throw new Error('Analysis failed');
      }

      const data = await response.json();

      // Add AI response
      const aiMessage = {
        type: 'ai',
        content: data.response,
        coin: data.detected_coin || 'Market',
        intent: data.detected_intent || 'Analysis',
        timestamp: new Date(data.timestamp * 1000).toLocaleString()
      };
      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        type: 'error',
        content: 'Sorry, something went wrong. Please try again.',
        timestamp: new Date().toLocaleString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex flex-col h-screen w-full bg-white shadow-lg">
      {/* Header */}
      <div className="flex justify-between items-center p-4 border-b">
        <div className="text-2xl font-bold text-blue-600">SageXbt</div>
        <div className="text-sm text-gray-500">Powered by DeepSeek</div>
      </div>

      {/* Messages Area */}
      <div className="flex-grow overflow-y-auto p-4 space-y-4">
        <div className="max-w-6xl mx-auto w-full">
          {messages.length === 0 ? (
            <Suggestions onSuggestionSelect={(suggestion) => {
              setQuery(suggestion);
              handleSubmit({ preventDefault: () => {} });
            }} />
          ) : (
            messages.map((message, index) => (
              <div 
                key={index} 
                className={`flex ${
                  message.type === 'user' 
                    ? 'justify-end' 
                    : 'justify-start'
                }`}
              >
                <div 
                  className={`max-w-[85%] p-3 rounded-lg ${
                    message.type === 'user'
                      ? 'bg-blue-500 text-white'
                      : message.type === 'error'
                      ? 'bg-red-100 text-red-800'
                      : 'bg-gray-100'
                  }`}
                >
                  {message.type === 'ai' && (
                    <div className="text-sm font-semibold text-blue-600 mb-1">
                      {message.coin} {message.intent && message.intent !== 'general' ? message.intent.toUpperCase() : 'Analysis'}
                    </div>
                  )}
                  
                  <div className="text-sm whitespace-pre-wrap">
                    {message.type === 'ai' 
                      ? formatMessage(message.content) 
                      : message.content}
                  </div>
                  
                  <div className="text-xs text-gray-400 mt-2 text-right">
                    {message.timestamp}
                  </div>
                </div>
              </div>
            ))
          )}
          
          {/* Loading indicator */}
          {isLoading && (
            <div className="flex justify-center items-center p-4">
              <div className="loader w-8 h-8 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin"></div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Input Area */}
      <form 
        onSubmit={handleSubmit} 
        className="p-4 border-t"
      >
        <div className="max-w-6xl mx-auto w-full flex items-center space-x-2">
          <input 
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Ask about cryptocurrency markets..."
            disabled={isLoading}
            className="flex-grow p-3 bg-gray-100 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button 
            type="submit" 
            disabled={isLoading || !query.trim()}
            className="bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition-colors disabled:opacity-50"
          >
            <Send size={20} />
          </button>
        </div>
      </form>
    </div>
  );
};

export default CryptoAnalyzerChat;
